import React from "react";
import Sidebar from "../components/navbar/Sidebar";
import Footer from "../components/footer/Footer";
import "./Login.css";
import { useNavigate } from "react-router-dom";
import {useState, useEffect} from "react";

function Login() {
  //redirecionamento para a pagina de registo
  const navigate = useNavigate();
  const registar = () => {
    navigate("/homePage");
  };

  //armazenando os inputs do formulario nas variaveis
  const [inputs, setInputs] = useState({
    username: "",
    password: ""
  });

  const handleChange = (event) => {
    const { name, value } = event.target;
    setInputs((prevState) => ({
      ...prevState,
      [name]: value
    }));
  };

  //faz fetch para 
  const handleSubmit = (event) => {
    event.preventDefault();

    fetch("http://localhost:8080/vanessa-vinicyus-proj3/rest/users/login", {
      method: "POST",
      headers: {
        "Accept": "*/*",
        "Content-type": "application/json"
      },
      body: JSON.stringify(inputs)
    })
    .then(function (response){
      if(response.status === 200) {
        const username = inputs.username;
        const token = response.text;

        //armazena no session storage o token
        sessionStorage.setItem("username", username);
        sessionStorage.setItem("token", token);

        alert("Bem vindo "+ inputs.username);
        navigate("/registo");
      } else {
        setInputs({
          username: "",
          password: ""
      })
        alert("user nao registado");
      }
    })
  }



  return (
    <div className="Login" id="login-outer-container">
      <div className="page-wrap" id="login-page-wrap">
        <main id="main-div" className="clearfix">
          <div className="login-form">
              <a href="homePage.html">
                  <img src="./assets/lettering.png" alt="lettering" height="180" />
              </a>

              <form id="login-form" onSubmit={handleSubmit}> 
                  <div className="form-group">
                      <label htmlFor="username">Username:</label>
                      <input type="text" id="username" name="username" placeholder="Insert your username" onChange={handleChange} required/>
                  </div>
                  <div className="form-group">
                      <label htmlFor="password">Password:</label>
                      <input type="password" id="password" name="password" placeholder="Insert your password" onChange={handleChange} required/>
                  </div>
                  <div className="buttons">
                      <input type="submit" value="Sign In" className="btn-signin" />
                      <button type="button" className="btn-signup" onClick={registar}>Sign up</button>
                  </div>
              </form>
          </div>
        </main>
        <Footer/>
      </div>
    </div>
  )
}

export default Login;
