import { create } from "zustand";
import { persist, createJSONStorage } from "zustand/middleware";


//Define the store
// export const userStore = create(
//   persist(
//     (set) => ({
//       username: "",
//       profilePicture: "",
//       updateName: (username) => set({username}), //function used to update the state variable
//       logout: () => set({username:null})
//     }), 
//     {
//       name: "mystore", //name to use for the persisted data   
//       storage: createJSONStorage(()=> sessionStorage)
//     }
//   )
// )

const useUserStore = create((set) => ({
  username: null,
  profilePicture: null,
  setUser: (user) => {
    console.log("Estado atualizado:", user);
    set(user);
  },
  logout: () => set({ username: null, profilePicture: null }),
}));
